arrayPipelines.data:
[
  {
    $group: {
      _id: null,
      totalTopics: { $sum: 1 },
			enTopics: {
        $sum: { $cond: [{ $eq: ["$lang", "en"] }, 1, 0] }
      },
			bnTopics: {
        $sum: { $cond: [{ $eq: ["$lang", "bn"] }, 1, 0] }
      },
      activeTopics: {
        $sum: { $cond: [{ $eq: ["$status", "active"] }, 1, 0] }
      },
      inactiveTopics: {
        $sum: { $cond: [{ $eq: ["$status", "inactive"] }, 1, 0] }
      }
    }
  },
  {
    $project: { _id: 0 }
  }
]

