arrayPipelines.data:
[
  {
    $match: { deletedAt: null }
  },
  {
    $group: {
      _id: null,
      totalTournaments: { $sum: 1 },
			      draftTournaments: {
        $sum: { $cond: [{ $eq: ["$status", "draft"] }, 1, 0] }
      },
      publishedTournaments: {
        $sum: { $cond: [{ $eq: ["$status", "published"] }, 1, 0] }
      },
      archivedTournaments: {
        $sum: { $cond: [{ $eq: ["$status", "archived"] }, 1, 0] }
      },
			enTournaments: {
        $sum: { $cond: [{ $eq: ["$lang", "en"] }, 1, 0] }
      },
			bnTournaments: {
        $sum: { $cond: [{ $eq: ["$lang", "bn"] }, 1, 0] }
      },
      freeTournaments: {
        $sum: { $cond: [{ $eq: ["$isFree", true] }, 1, 0] }
      },
      paidTournaments: {
        $sum: { $cond: [{ $eq: ["$isFree", false] }, 1, 0] }
      },
      totalPlayers: { $sum: "$playersCount" }
    }
  },
  {
    $project: { _id: 0 }
  }
]

